import 'whatwg-fetch';

class HttpServices {
    getProducts = () => {
        var promise = new Promise((resolve, reject) => {
            fetch("https://localhost:3004/product")
            .then(response => {
                resolve(res.json());
            })
            return promise;
        });
    }
}

export default HttpServices;
